        method: 'PUT',
        headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(sample),
    });
    return response.json();
};

/**
 * Fetches all locations from the API.
 * @returns {Promise} - Promise resolving to an array of all locations.
 */
