import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Route, Routes, NavLink, Link } from 'react-router-dom';
import Header from './../components/Header';
import Footer from './../components/Footer';
import Navbar from './../components/navbar';
import './../starterstyles.css';
import SampleShare from './SampleShare';
import SampleCreateEdit from './SampleCreateEdit';

const baseURL = 'https://comp2140.uqcloud.net/api/sample/';

const sampleID = null;

const strSampleID = sampleID ? sampleID.toString() : "";

const APIKEY = 'gdJY8BdnNf';

const url = `${baseURL}${strSampleID}/?api_key=${APIKEY}`;

function SampleList () {


    const [samples, setSamples] = useState([]);

    const [isLoading, setIsLoading] = useState(true);


    /* useEffect(() => {
        const fetchSample = async () => {
            try {
                const APIResponse = await fetch(url);
                const json = await APIResponse.json();
                setSample(json);
            } catch (error) {
                // error handling
                console.log("error", error);
            }
        }

        // call the async fetchSample function
        fetchSample();
    }, []) */

    // fetch sample data from Django, parsed as json
    useEffect(() => {
        const fetchSample = async() => {
            setIsLoading(true); // Start loading
            const APIResponse = await fetch(url); // api?
            const json = await APIResponse.json();
            setSamples(json);
            setIsLoading(false); // End loading
        };

        // call the async fetchSample function
        fetchSample();
    }, []);

    return (
        <>
            <h2 className="title">Your Song Samples</h2>
            <div className="create-card">
                <Link to="/SampleCreateEdit" className="bright-button">Create Sample</Link>
            </div>

            <section className="sample">
            {isLoading ? (
                <p>Loading...</p>
            ) : (
                samples.map((sample) => (
                    <div className="card">
                        <div className="song-details">
                            <h3>{sample.name}</h3>
                            <p>Date Created: {sample.datetime}</p>
                        </div>
                        <div class="button-group-container">
                            <Link to="/SampleShare" className="button">Share</Link>
                            <button className="button">Preview</button>
                            <Link to="/SampleCreateEdit" className="bright-button">Edit</Link>
                        </div>
                    </div>
                ))
            )}
        </section>

        <div className="create-card">
            <Link to="/SampleCreateEdit" className="bright-button">Create Sample</Link>
        </div>
    </>
    );
}

export default SampleList;
